import React, { useState } from 'react';
import { ShoppingCart, Menu, ChefHat, Apple, BarChart3, Settings } from 'lucide-react';

// Composant Sidebar
const Sidebar = ({ currentPage, onNavigate }) => {
  const menuItems = [
    { id: 'commandes', label: 'Commandes', icon: ShoppingCart },
    { id: 'menu', label: 'Gestion du menu', icon: Menu },
    { id: 'kitchen', label: 'Gestion de la cuisine', icon: ChefHat },
    { id: 'ingredients', label: 'Ingrédients', icon: Apple },
    { id: 'statistics', label: 'Statistiques & Analyses', icon: BarChart3 },
    { id: 'settings', label: 'Paramètres', icon: Settings },
  ];

  return (
    <aside className="w-64 bg-white shadow-lg h-screen fixed">
      <div className="p-6">
        <h2 className="text-xl font-bold text-gray-800 mb-6">Cuisine Vital'</h2>
      </div>
      <nav className="mt-6">
        {menuItems.map((item) => {
          const Icon = item.icon;
          return (
            <button
              key={item.id}
              onClick={() => onNavigate(item.id)}
              className={`w-full text-left px-6 py-3 text-sm font-medium flex items-center space-x-3 transition-colors ${
                currentPage === item.id
                  ? 'bg-blue-100 text-blue-700'
                  : 'text-gray-600 hover:bg-gray-100'
              }`}
            >
              <Icon size={18} />
              <span>{item.label}</span>
            </button>
          );
        })}
      </nav>
      <div className="absolute bottom-6 left-6 flex items-center space-x-3">
        <img src="https://via.placeholder.com/30" alt="Utilisateur" className="rounded-full" />
        <span className="text-sm text-gray-700">Tsihoaina Berthin</span>
        <span className="text-xs text-gray-500">Admin User</span>
      </div>
    </aside>
  );
};

// Composant Dashboard
const Dashboard = () => (
  <div className="ml-64 p-6">
    <h2 className="text-2xl font-bold text-gray-800 mb-6">Bonjour, Berthin!</h2>
    <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
      <div className="bg-white p-6 rounded-lg shadow">
        <h3 className="text-lg font-semibold text-gray-700 mb-4">Commandes en cours</h3>
        <p className="text-3xl font-bold text-blue-600">156</p>
        <p className="text-sm text-green-600 mt-2">+12% depuis hier</p>
      </div>
      <div className="bg-white p-6 rounded-lg shadow">
        <h3 className="text-lg font-semibold text-gray-700 mb-4">Revenus journaliers</h3>
        <p className="text-3xl font-bold text-green-600">80,000 Ar</p>
        <p className="text-sm text-green-600 mt-2">+5% d’aujourd’hui</p>
      </div>
      <div className="bg-white p-6 rounded-lg shadow">
        <h3 className="text-lg font-semibold text-gray-700 mb-4">Plats les plus vendus</h3>
        <div className="space-y-2">
          <div className="flex items-center space-x-3">
            <img src="https://via.placeholder.com/40" alt="Plat" className="rounded" />
            <span>Plat 1</span>
            <span className="ml-auto text-gray-600">500 Commandes</span>
          </div>
        </div>
      </div>
      <div className="bg-white p-6 rounded-lg shadow">
        <h3 className="text-lg font-semibold text-gray-700 mb-4">Workflow en cuisine</h3>
        <div className="space-y-2">
          <div className="flex justify-between">
            <span>En préparation</span>
            <span className="text-gray-600">12 commandes</span>
          </div>
          <div className="w-full bg-gray-200 rounded-full h-2.5">
            <div className="bg-blue-600 h-2.5 rounded-full" style={{ width: '60%' }}></div>
          </div>
          <div className="flex justify-between">
            <span>Prêtes</span>
            <span className="text-gray-600">5 commandes</span>
          </div>
          <div className="w-full bg-gray-200 rounded-full h-2.5">
            <div className="bg-purple-600 h-2.5 rounded-full" style={{ width: '30%' }}></div>
          </div>
        </div>
      </div>
    </div>
  </div>
);

// Composant principal App
const App = () => {
  const [currentPage, setCurrentPage] = useState('commandes');

  const renderPage = () => {
    switch (currentPage) {
      case 'commandes':
      case 'menu':
      case 'kitchen':
      case 'ingredients':
      case 'statistics':
      case 'settings':
        return <Dashboard />;
      default:
        return <Dashboard />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex">
      <Sidebar currentPage={currentPage} onNavigate={setCurrentPage} />
      <main className="flex-1">{renderPage()}</main>
    </div>
  );
};

export default App;
