import { useState } from "react";

// exemple de données de fields
const fields = [
  { name: "name", label: "Nom", type: "text" },
  { name: "email", label: "Email", type: "email" },
  { name: "contact", label: "Contact", type: "text" },
  {
    name: "profil",
    label: "Profil",
    type: "select",
    options: [
      { label: "Étudiant", value: "etudiant" },
      { label: "Enseignant", value: "enseignant" },
      { label: "Personnel", value: "personnel" },
    ],
  },
];

const Login = ({ title, fields, handleSubmit }) => {
  const [formData, setFormData] = useState({});

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  return (
    <div className="fixed top-0 left-0 w-screen h-screen bg-black/20 flex items-center justify-center z-50">
      <div className="p-10 bg-white rounded-xl w-full max-w-md">
        <h2 className="text-2xl text-gray-800 font-semibold mb-6">{title}</h2>
        <form onSubmit={(e) => handleSubmit(e, formData)} className="flex flex-col gap-4">
          {fields.map((field, index) => (
            <div key={index} className="flex flex-col gap-1">
              <label htmlFor={field.name}>{field.label || field.name}</label>

              {field.type === "select" ? (
                <select
                  name={field.name}
                  id={field.name}
                  value={formData[field.name] || ""}
                  onChange={handleChange}
                  className="bg-gray-100 rounded-xl p-2"
                >
                  <option value="">-- Choisir --</option>
                  {field.options.map((opt, idx) => (
                    <option key={idx} value={opt.value}>
                      {opt.label}
                    </option>
                  ))}
                </select>
              ) : (
                <input
                  type={field.type}
                  name={field.name}
                  id={field.name}
                  value={formData[field.name] || ""}
                  onChange={handleChange}
                  className="bg-gray-100 rounded-xl p-2"
                />
              )}
            </div>
          ))}

          <button
            type="submit"
            className="mt-4 rounded-xl bg-gray-950/80 text-white py-2"
          >
            Envoyer
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
