  livraisonsDansOrdre: [
    { id: "aaaaaaaa-aaaa-aaaa-aaaa-aaaaaaaaaaaa", adresse: "Palais de la Reine (Rova)", statut: "ASSIGNEE", localisation: "Point(47.5323 -18.9227)" },
    { id: "dddddddd-dddd-dddd-dddd-dddddddddddd", adresse: "Gare de Soarano", statut: "ASSIGNEE", localisation: "Point(47.5222 -18.9038)" },
    { id: "bbbbbbbb-bbbb-bbbb-bbbb-bbbbbbbbbbbb", adresse: "Marché d'Analakely", statut: "ASSIGNEE", localisation: "Point(47.5255 -18.9069)" },
    { id: "cccccccc-cccc-cccc-cccc-cccccccccccc", adresse: "Lac Anosy", statut: "ASSIGNEE", localisation: "Point(47.5186 -18.9171)" },
  ],
