// --- CONFIGURATION DE LA CARTE ---
const mapContainerStyle = { width: '100vw', height: '70vh' };
const initialCenter = { lat: -18.91368, lng: 47.53613 }; // Antananarivo

// --- FONCTION UTILITAIRE ---
const parseCoords = (localisation) => {
  if (!localisation) return null;
  const [lng, lat] = localisation.replace('Point(', '').replace(')', '').split(' ');
  return { lat: parseFloat(lat), lng: parseFloat(lng) };
};


function App() {
  const [map, setMap] = useState(null);
  const [livreurs, setLivreurs] = useState([]);
  const [selectedLivreurId, setSelectedLivreurId] = useState('');
  const [routeData, setRouteData] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  // 1. Charger l'API Google Maps avec la bibliothèque 'geometry'
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: 'AIzaSyAOVYRIgupAurZup5y1PRh8Ismb1A3lLao',
    libraries: ['geometry'],
  });

  // 2. Charger les livreurs simulés au démarrage
  useEffect(() => {
    setLivreurs(mockLivreurs);
    if (mockLivreurs.length > 0) {
      setSelectedLivreurId(mockLivreurs[0].id);
    }
  }, []);

  // 3. Simuler le calcul d'itinéraire au clic
  const handleCalculateRoute = () => {
    if (!selectedLivreurId) return;
    setIsLoading(true);
    setError('');
    setRouteData(null);

    // Simulation d'un délai réseau pour un effet réaliste
    setTimeout(() => {
      if (selectedLivreurId === '11111111-1111-1111-1111-111111111111') {
        setRouteData(mockRouteData);
      } else {
        setError('Aucun itinéraire à calculer pour ce livreur.');
      }
      setIsLoading(false);
    }, 1000);
  };

  // 4. Décoder la polyline quand les données sont prêtes
  const decodedPath = useMemo(() => {
    if (!routeData?.geometrieItineraire || !isLoaded) return [];
    try {
      return window.google.maps.geometry.encoding.decodePath(routeData.geometrieItineraire);
    } catch (e) {
      console.error("Erreur lors du décodage de la polyline:", e);
      return [];
    }
  }, [routeData, isLoaded]);

  // 5. Fonctions pour gérer l'instance de la carte
  const onLoad = useCallback(function callback(mapInstance) {
    setMap(mapInstance);
  }, []);

  const onUnmount = useCallback(function callback(mapInstance) {
    setMap(null);
  }, []);

  // 6. Effet pour zoomer automatiquement sur l'itinéraire
  useEffect(() => {
    if (map && decodedPath.length > 0) {
      const bounds = new window.google.maps.LatLngBounds();
      decodedPath.forEach(latLng => {
        bounds.extend(latLng);
      });
      map.fitBounds(bounds);
    }
  }, [map, decodedPath]);

  // 7. Rendu du composant
  return (
    <div className="App">
      <header className="App-header">
        <h1>Calculateur d'Itinéraire Livreur (Mode Simulation)</h1>
        <div className="controls">
          <select 
            value={selectedLivreurId} 
            onChange={(e) => setSelectedLivreurId(e.target.value)}
            disabled={isLoading}
          >
            <option value="">-- Sélectionnez un livreur --</option>
            {livreurs.map(livreur => (
              <option key={livreur.id} value={livreur.id}>
                {livreur.prenom} {livreur.nom}
              </option>
            ))}
          </select>
          <button onClick={handleCalculateRoute} disabled={isLoading || !selectedLivreurId}>
            {isLoading ? 'Calcul en cours...' : "Calculer l'itinéraire"}
          </button>
        </div>
        {error && <p className="error">{error}</p>}
      </header>

      <main>
        {isLoaded ? (
          <GoogleMap
            mapContainerStyle={mapContainerStyle}
            center={initialCenter}
            zoom={14}
            onLoad={onLoad}
            onUnmount={onUnmount}
          >
            {/* Affiche l'itinéraire si disponible */}
            {decodedPath.length > 0 && (
              <Polyline
                path={decodedPath}
                options={{
                  strokeColor: '#FF0000',
                  strokeOpacity: 0.8,
                  strokeWeight: 5,
                  zIndex: 1,
                }}
              />
            )}
            {/* Affiche les marqueurs si disponibles */}
            {routeData?.livraisonsDansOrdre?.map((livraison, index) => {
              const position = parseCoords(livraison.localisation);
              return position ? (
                <Marker
                  key={livraison.id}
                  position={position}
                  label={`${index + 1}`}
                  title={`${index + 1}. ${livraison.adresse}`}
                />
              ) : null;
            })}
          </GoogleMap>
        ) : (
          <div>Chargement de la carte...</div>
        )}
        
        {/* Affiche les résultats textuels si disponibles */}
        {routeData && !error && (
          <div className="results">
            <h2>Résultats de l'itinéraire</h2>
            <p><strong>Distance totale:</strong> {(routeData.distanceTotaleMetres / 1000).toFixed(2)} km</p>
