            <h3>Ordre des livraisons:</h3>
            <ol>
              {routeData.livraisonsDansOrdre?.map(l => (
                <li key={l.id}>{l.adresse}</li>
              ))}
            </ol>
          </div>
        )}
      </main>
    </div>
  );
}

