// Composant principal App
const App = () => {
  const [currentPage, setCurrentPage] = useState('commandes');

  const renderPage = () => {
    switch (currentPage) {
      case 'commandes':
      case 'plats':
      case 'categories':
      case 'nouveau-plat':
      case 'promotions':
        return <Dashboard />;
      default:
        return <Dashboard />;
    }
  };

  return (
    <div className="app">
      <div className="app-content">
        <Sidebar currentPage={currentPage} onNavigate={setCurrentPage} />
        <main className="main-content">
          {renderPage()}
        </main>
      </div>
    </div>
  );
};

